        public static S Fold<V, S>(this ImMap<V> map, S state, Func<ImMapEntry<V>, S, S> reduce, Stack<ImMap<V>> parents = null)
        {
            if (map == ImMap<V>.Empty)
                return state;
            if (map is ImMapEntry<V> v)
                return reduce(v, state);

            var count = 0;
            while (true)
            {
                if (map is ImMap<V>.Branch2 b2)
