                        swap = e1; e1 = p; p = swap;
                        if (ph < e0.Hash)
                        {
                            swap = e0; e0 = e1; e1 = swap;
                        }
                    }

                    state = reduce((ImMapEntry<V>)e0, state); // todo: @perf non need for the casting when we are done with the proper Remove 
                    state = reduce((ImMapEntry<V>)e1, state);
                    state = reduce((ImMapEntry<V>)p,  state);
