                    state = reduce((ImMapEntry<V>)e0, state);
                    state = reduce((ImMapEntry<V>)e1, state);
                    state = reduce((ImMapEntry<V>)pp, state);
                    state = reduce((ImMapEntry<V>)p,  state);
